                boxSize += stream.ReadUInt24(out this.num_roi);

                this.top_left_mb = new uint[num_roi];
                this.roi_mb_width = new byte[num_roi];
                this.roi_mb_height = new byte[num_roi];
                for (int i = 0; i < num_roi; i++)
