                boxSize += stream.WriteUInt8(this.grid_roi_mb_width);
                boxSize += stream.WriteUInt8(this.grid_roi_mb_height);
            }

            else if (iroi_type == 1)
            {
                boxSize += stream.WriteUInt24(this.num_roi);

                for (int i = 0; i < num_roi; i++)
                {
                    boxSize += stream.WriteUInt32(this.top_left_mb[i]);
                    boxSize += stream.WriteUInt8(this.roi_mb_width[i]);
                    boxSize += stream.WriteUInt8(this.roi_mb_height[i]);
                }
            }
            return boxSize;
        }

        public override ulong CalculateSize()
        {
            ulong boxSize = 0;
            boxSize += base.CalculateSize();
            boxSize += 2; // iroi_type
            boxSize += 6; // reserved

            if (iroi_type == 0)
            {
                boxSize += 8; // grid_roi_mb_width
                boxSize += 8; // grid_roi_mb_height
            }

            else if (iroi_type == 1)
