                boxSize += 24; // num_roi

                for (int i = 0; i < num_roi; i++)
                {
                    boxSize += 32; // top_left_mb
                    boxSize += 8; // roi_mb_width
                    boxSize += 8; // roi_mb_height
                }
            }
            return boxSize;
        }
    }


